import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './SignUp.css';  // Import your CSS file

function SignUp() {
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [otpSent, setOtpSent] = useState(false);  // Track OTP sent state
  const navigate = useNavigate();

  const handleSendOtp = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/auth/send-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });
      const data = await response.json();
      if (response.ok) {
        setOtpSent(true);  // OTP sent successfully
      } else {
        alert(data.error);
      }
    } catch (err) {
      console.error(err);
    }
  };

  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:5000/api/auth/verify-otp', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, otp }),
      });
      const data = await response.json();
      if (response.ok) {
        alert('OTP Verified!');
        navigate('/dashboard');  // Redirect on successful OTP verification
      } else {
        alert(data.error);
      }
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="signup-page">
      <form className="signup-form">
        <h2>Sign Up</h2>

        {/* Email input */}
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />

        {!otpSent ? (
          // Send OTP button
          <button onClick={handleSendOtp} className="send-otp-button">
            Send OTP
          </button>
        ) : (
          // OTP input and verification
          <>
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              required
            />
            <button onClick={handleVerifyOtp} className="verify-otp-button">
              Verify OTP
            </button>
          </>
        )}
      </form>
    </div>
  );
}

export default SignUp;
