import React, { useState } from 'react';
import { FaStar, FaUser, FaBuilding } from 'react-icons/fa';
import axios from 'axios'; // Added axios import
import './ReviewForm.css';

const ReviewForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    company: '',
    rating: 0,
    review: '',
    email: ''
  });
  const [hoverRating, setHoverRating] = useState(0);
  const [submissionStatus, setSubmissionStatus] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRatingChange = (rating) => {
    setFormData({ ...formData, rating });
  };

  const validateForm = () => {
    if (!formData.email.match(/^[\w-]+(\.[\w-]+)*@([\w-]+\.)+[a-zA-Z]{2,7}$/)) {
      return 'Please enter a valid email address';
    }
    if (formData.rating < 1) {
      return 'Please select a rating between 1 and 5 stars';
    }
    return null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationError = validateForm();
    if (validationError) {
      setSubmissionStatus({ success: false, message: validationError });
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/api/reviews', formData);
      setSubmissionStatus({ 
        success: true, 
        message: 'Review submitted successfully!' 
      });
      
      // Reset form
      setFormData({
        name: '',
        company: '',
        rating: 0,
        review: '',
        email: ''
      });
      setHoverRating(0);

    } catch (error) {
      const errorMessage = error.response?.data?.error || 'Error submitting review. Please try again.';
      setSubmissionStatus({ 
        success: false, 
        message: errorMessage 
      });
      console.error('Error submitting review:', error);
    }
  };

  return (
    <div className="review-form-container">
      <h2>Share Your Experience</h2>
      <p>We value your honest feedback about our products and services.</p>
      
      {submissionStatus && (
        <div className={`submission-message ${submissionStatus.success ? 'success' : 'error'}`}>
          {submissionStatus.message}
          <button 
            onClick={() => setSubmissionStatus(null)}
            className="close-button"
          >
            Ã—
          </button>
        </div>
      )}

      <form onSubmit={handleSubmit} className="review-form">
        {/* Form fields remain the same */}
        {/* ... rest of your form JSX ... */}
      </form>
    </div>
  );
};

export default ReviewForm;
