import React, { useState, useEffect } from 'react';

function UpdateGreaseProductForm({ productId, onUpdateProduct }) {
    const [productData, setProductData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                if (!productId) {
                    setError('No product ID provided');
                    return;
                }
                
                const response = await fetch(`http://localhost:5000/api/grease/${productId}`);
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setProductData(data);
            } catch (err) {
                console.error('Fetch error:', err);
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchProduct();
    }, [productId]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setProductData(prev => ({
            ...prev,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`http://localhost:5000/api/grease/${productId}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(productData),
            });

            const data = await response.json();
            
            if (!response.ok) {
                throw new Error(data.error || 'Failed to update product');
            }

            alert('Product updated successfully!');
            onUpdateProduct();
        } catch (err) {
            console.error('Update error:', err);
            alert(err.message || 'An error occurred while updating the product');
        }
    };

    if (loading) return <div>Loading product details...</div>;
    if (error) return <div>Error: {error}</div>;
    if (!productData) return <div>No product data found</div>;

    return (
        <div className="update-form-container">
            <h2>Update Grease Product</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Product Name:</label>
                    <input
                        type="text"
                        name="name"
                        value={productData.name || ''}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Category:</label>
                    <input
                        type="text"
                        name="category"
                        value={productData.category || ''}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Price:</label>
                    <input
                        type="number"
                        name="price"
                        value={productData.price || 0}
                        onChange={handleChange}
                        min="0"
                        step="0.01"
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Stock Quantity:</label>
                    <input
                        type="number"
                        name="stock"
                        value={productData.stock || 0}
                        onChange={handleChange}
                        min="0"
                        required
                    />
                </div>

                <div className="form-group">
                    <label>Image URL:</label>
                    <input
                        type="text"
                        name="image"
                        value={productData.image || ''}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div className="form-group checkbox-group">
                    <label>
                        <input
                            type="checkbox"
                            name="offer"
                            checked={productData.offer || false}
                            onChange={handleChange}
                        />
                        On Offer
                    </label>
                </div>

                <button type="submit" className="submit-button">
                    Update Product
                </button>
            </form>
        </div>
    );
}

export default UpdateGreaseProductForm;
