import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
function DeleteGreaseProductForm({ productId, onDeleteProduct }) {
    const [productData, setProductData] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            const response = await fetch(`http://localhost:5000/api/grease/${productId}`);
            const data = await response.json();
            setProductData(data);
        };

        if (productId) {
            fetchProduct();
        }
    }, [productId]);

    const handleDelete = async () => {
        if (window.confirm('Are you sure you want to delete this product?')) {
            try {
                const response = await fetch(`http://localhost:5000/api/grease/${productId}`, {
                    method: 'DELETE',
                });
                if (response.ok) {
                    const data = await response.json();
                    alert(data.message || 'Product deleted successfully!');
                    onDeleteProduct();  // Refresh products after deletion
                } else {
                    const data = await response.json();
                    alert(data.error || 'Failed to delete product');
                }
            } catch (error) {
                alert('An error occurred while deleting the product');
            }
        }
    };

    if (!productData) return null;

    return (
        <div>
            <h3>Delete Product: {productData.name}</h3>
            <p>Are you sure you want to delete this product?</p>
            <p><strong>Category:</strong> {productData.category}</p>
            <p><strong>Price:</strong> ${productData.price}</p>
            <p><strong>Stock:</strong> {productData.stock}</p>
            <p><strong>Offer:</strong> {productData.offer ? 'Yes' : 'No'}</p>
            <img src={productData.image} alt={productData.name} width="100" />
            <button onClick={handleDelete}>Delete Product</button>
        </div>
    );
}

export default DeleteGreaseProductForm;
