import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './AdminDashboard.css';
import AddGreaseProductForm from './AddGreaseProductForm';
import UpdateGreaseProductForm from './UpdateGreaseProductForm';
import DeleteGreaseProductForm from './DeleteGreaseProductForm';

function AdminDashboard() {
    const [enquiries, setEnquiries] = useState([]);
    const [greaseProducts, setGreaseProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [isAddingProduct, setIsAddingProduct] = useState(false);
    const [productName, setProductName] = useState('');
    const [productCategory, setProductCategory] = useState('');

    const navigate = useNavigate();

    useEffect(() => {
        const fetchEnquiries = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/contact');
                const data = await response.json();
                if (response.ok) {
                    setEnquiries(data);
                    setLoading(false);
                } else {
                    throw new Error(data.message || 'Failed to fetch enquiries');
                }
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        const fetchGreaseProducts = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/grease');
                const data = await response.json();
                if (response.ok) {
                    setGreaseProducts(data);
                    setLoading(false);
                } else {
                    throw new Error(data.message || 'Failed to fetch grease products');
                }
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        fetchEnquiries();
        fetchGreaseProducts();
    }, []);

    const handleLogout = () => {
        localStorage.clear();
        sessionStorage.clear();
        navigate('/');
    };

    const handleAddProduct = () => {
        setIsAddingProduct(true);
    };

    const handleDeleteProduct = () => {
        setSelectedProduct({ name: productName, category: productCategory });
        // Optionally refresh products after delete
    };

    const handleUpdateProduct = () => {
        setSelectedProduct({ name: productName, category: productCategory });
        // Optionally refresh products after update
    };

    return (
        <div className="admin-container">
            <div className="dashboard-header">
                <h2>Admin Dashboard</h2>
                <button className="logout-button" onClick={handleLogout}>Logout</button>
            </div>

            {loading && <p>Loading...</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <div>
                <h3>Grease Products</h3>
                <button onClick={handleAddProduct}>Add New Grease Product</button>
                {isAddingProduct && <AddGreaseProductForm onAddProduct={() => setIsAddingProduct(false)} />}

                {/* Product Selection Section for Update/Delete */}
                <div className="update-delete-section">
                    <h3>Update or Delete Product</h3>
                    <div>
                        <label>Select Category</label>
                        <select 
                            value={productCategory} 
                            onChange={(e) => setProductCategory(e.target.value)}>
                            <option value="">Select Category</option>
                            {/* You can dynamically render categories if needed */}
                            <option value="Grease">Grease</option>
                            <option value="Blocks">Blocks</option>
                            <option value="Bearings">Bearings</option>
                        </select>
                    </div>
                    <div>
                        <label>Enter Product Name</label>
                        <input 
                            type="text" 
                            value={productName} 
                            onChange={(e) => setProductName(e.target.value)} 
                            placeholder="Enter Product Name"
                        />
                    </div>
                    <div>
                        <button onClick={handleUpdateProduct}>Update Product</button>
                        <button onClick={handleDeleteProduct}>Delete Product</button>
                    </div>
                </div>

                {/* Conditionally render update & delete forms if a product is selected */}
                {selectedProduct && (
                    <>
                        <UpdateGreaseProductForm
                            product={selectedProduct}
                            onUpdateProduct={handleUpdateProduct}
                        />
                        <DeleteGreaseProductForm
                            product={selectedProduct}
                            onDeleteProduct={handleDeleteProduct}
                        />
                    </>
                )}

                {greaseProducts.length === 0 ? (
                    <p>No grease products found.</p>
                ) : (
                    <ul className="product-list">
                        {greaseProducts.map((product) => (
                            <li
                                key={product._id}
                                className="product-card"
                                onClick={() => {
                                    setProductName(product.name);
                                    setProductCategory(product.category);
                                }}
                            >
                                <h4>{product.name}</h4>
                                <p>Price: ${product.price}</p>
                                <p>Stock: {product.stock}</p>
                                <p>Category: {product.category}</p>
                                <p>Offer: {product.offer ? 'Yes' : 'No'}</p>
                                <img src={product.image} alt={product.name} width="100" />
                                <p><i>Click card to update/delete</i></p>
                            </li>
                        ))}
                    </ul>
                )}
            </div>

            <div>
                <h3>Customer Enquiries</h3>
                {enquiries.length === 0 ? (
                    <p>No enquiries found.</p>
                ) : (
                    <ul className="enquiry-list">
                        {enquiries.map((enquiry, index) => (
                            <li key={index} className="enquiry-card">
                                <p><strong>Name:</strong> {enquiry.name}</p>
                                <p><strong>Email:</strong> {enquiry.email}</p>
                                <p><strong>Message:</strong> {enquiry.message}</p>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
}

export default AdminDashboard;
