// src/pages/Products.js
import React, { useEffect, useState } from 'react';
import './Product.css';

function Products() {
    const [category, setCategory] = useState('products');
    const [products, setProducts] = useState([]);
    const [activeBtn, setActiveBtn] = useState('Bearings');

    const categoryMap = {
        Bearings: 'products',
        Grease: 'grease',
        Blocks: 'blocks',
    };

    useEffect(() => {
        fetchProducts();
    }, [category]);

    const fetchProducts = async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/${category}`);
            const data = await response.json();
            setProducts(data);
        } catch (error) {
            console.error('Error fetching products:', error);
        }
    };

    const handleCategoryChange = (selectedCategory) => {
        setCategory(categoryMap[selectedCategory]);
        setActiveBtn(selectedCategory);
    };

    return (
        <div className="products-page">
            <h2>Our Products</h2>
            <p className="products-description">
                Browse our wide range of products across multiple categories.
            </p>

            <div className="category-buttons">
                {Object.keys(categoryMap).map((cat) => (
                    <button
                        key={cat}
                        className={`category-btn ${activeBtn === cat ? 'active' : ''}`}
                        onClick={() => handleCategoryChange(cat)}
                    >
                        {cat}
                    </button>
                ))}
            </div>

            <div className="products-grid">
                {products.map((product, index) => (
                    <div
                        key={index}
                        className={`product-card ${product.offer ? 'offer' : ''}`}
                    >
                        <img
                            src={product.image}
                            alt={product.name}
                            className="product-image"
                        />
                        {product.offer && <div className="offer-badge">Special Offer</div>}
                        <h3 className="product-title">{product.name}</h3>
                        <p>Category: {product.category}</p>
                        <p className="product-price">${product.price}</p>
                        <button className="buy-now-button">Buy Now</button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default Products;
