import React, { useState } from 'react';
import { FaStar, FaUser, FaBuilding } from 'react-icons/fa';
import axios from 'axios';
import './reviewform.css';

const ReviewForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    company: '',
    rating: 0,
    review: '',
    email: ''
  });
  const [hoverRating, setHoverRating] = useState(0);
  const [submissionStatus, setSubmissionStatus] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRatingChange = (rating) => {
    setFormData({ ...formData, rating });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/reviews', formData);
      setSubmissionStatus({ success: true, message: 'Review submitted successfully!' });
      setFormData({
        name: '',
        company: '',
        rating: 0,
        review: '',
        email: ''
      });
    } catch (error) {
      setSubmissionStatus({ success: false, message: 'Error submitting review. Please try again.' });
      console.error('Error submitting review:', error);
    }
  };

  return (
    <div className="review-form-container">
      <h2>Share Your Experience</h2>
      <p>We value your honest feedback about our products and services.</p>
      
      {submissionStatus && (
        <div className={`submission-message ${submissionStatus.success ? 'success' : 'error'}`}>
          {submissionStatus.message}
        </div>
      )}

      <form onSubmit={handleSubmit} className="review-form">
        <div className="form-group">
          <label htmlFor="name">
            <FaUser className="input-icon" /> Your Name
          </label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">
            <FaUser className="input-icon" /> Email Address
          </label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="company">
            <FaBuilding className="input-icon" /> Company Name
          </label>
          <input
            type="text"
            id="company"
            name="company"
            value={formData.company}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group rating-group">
          <label>Your Rating</label>
          <div className="star-rating">
            {[...Array(5)].map((_, index) => {
              const ratingValue = index + 1;
              return (
                <label key={index}>
                  <input
                    type="radio"
                    name="rating"
                    value={ratingValue}
                    onClick={() => handleRatingChange(ratingValue)}
                  />
                  <FaStar
                    className="star"
                    color={ratingValue <= (hoverRating || formData.rating) ? '#ffc107' : '#e4e5e9'}
                    size={30}
                    onMouseEnter={() => setHoverRating(ratingValue)}
                    onMouseLeave={() => setHoverRating(0)}
                  />
                </label>
              );
            })}
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="review">Your Review</label>
          <textarea
            id="review"
            name="review"
            value={formData.review}
            onChange={handleChange}
            rows="5"
            required
          />
        </div>

        <button type="submit" className="submit-button">
          Submit Review
        </button>
      </form>
    </div>
  );
};

export default ReviewForm;
