import React, { useState, useEffect } from 'react';

function DeleteGreaseProductForm({ product, onClose, onSuccess }) {
    const [isDeleting, setIsDeleting] = useState(false);
    const [error, setError] = useState('');

    const handleDelete = async () => {
        if (!window.confirm(`Are you sure you want to delete ${product.name}?`)) {
            return;
        }

        try {
            setIsDeleting(true);
            setError('');
            
            const response = await fetch(`http://localhost:5000/api/grease/${product._id}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                },
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || 'Failed to delete product');
            }

            const data = await response.json();
            alert(data.message || 'Product deleted successfully!');
            onSuccess(); // This will refresh the product list
        } catch (err) {
            setError(err.message || 'An error occurred while deleting the product');
        } finally {
            setIsDeleting(false);
        }
    };

    return (
        <div className="delete-form-container">
            <h3>Delete Product: {product.name}</h3>
            {error && <p className="error-message">{error}</p>}
            
            <div className="product-details">
                <p><strong>Category:</strong> {product.category}</p>
                <p><strong>Price:</strong> ${product.price}</p>
                <p><strong>Stock:</strong> {product.stock}</p>
                <p><strong>Offer:</strong> {product.offer ? 'Yes' : 'No'}</p>
                <img src={product.image} alt={product.name} width="100" />
            </div>
            
            <div className="form-actions">
                <button 
                    onClick={handleDelete} 
                    disabled={isDeleting}
                    className="delete-button"
                >
                    {isDeleting ? 'Deleting...' : 'Confirm Delete'}
                </button>
                <button 
                    onClick={onClose} 
                    className="cancel-button"
                >
                    Cancel
                </button>
            </div>
        </div>
    );
}

export default DeleteGreaseProductForm;
