import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Products.css';

function Products() {
    const [products, setProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('bearings');
    const navigate = useNavigate();

    const handleLogout = () => {
        navigate('/signin');
    };

    const fetchData = async (category) => {
        let endpoint = '';
        if (category === 'bearings') endpoint = 'products';
        else if (category === 'grease') endpoint = 'grease';
        else if (category === 'blocks') endpoint = 'blocks';

        try {
            const res = await fetch(`http://localhost:5000/api/${endpoint}`);
            const data = await res.json();
            setProducts(data);
        } catch (err) {
            console.error("Error fetching data:", err);
        }
    };

    useEffect(() => {
        fetchData(selectedCategory);
    }, [selectedCategory]);

    return (
        <div className="products-wrapper">
            <header className="navbar fixed-navbar">
                <div className="navbar-brand">Mehta Enterprise</div>
                <nav>
                    <ul className="navbar-links">
                        <li><Link to="/home">Home</Link></li>
                        <li><Link to="/products">Products</Link></li>
                        <li><Link to="/contact">Contact Us</Link></li>
                        <li><button className="logout-button" onClick={handleLogout}>Logout</button></li>
                    </ul>
                </nav>
            </header>

            <main className="products-page">
                <h2>Our Products</h2>
                <p className="products-description">
                    Browse our wide range of products across multiple categories.
                </p>

                <div className="category-buttons">
                    {['bearings', 'grease', 'blocks'].map((cat) => (
                        <button
                            key={cat}
                            className={`category-btn ${selectedCategory === cat ? 'active' : ''}`}
                            onClick={() => setSelectedCategory(cat)}
                        >
                            {cat.charAt(0).toUpperCase() + cat.slice(1)}
                        </button>
                    ))}
                </div>

                <div className="products-grid">
                    {products.map((product) => (
                        <div key={product._id} className={`product-card ${product.offer ? 'offer' : ''}`}>
                            <img src={product.image} alt={product.name} className="product-image" />
                            <h3 className="product-title">{product.name}</h3>
                            <p>Category: {product.category}</p>
                            <p className="product-price">
                                {product.originalPrice && (
                                    <span className="original-price">${product.originalPrice}</span>
                                )}
                                ${product.price}
                            </p>
                            {product.offer && <span className="offer-badge">Special Offer</span>}
                            <button className="buy-now-button">Buy Now</button>
                        </div>
                    ))}
                </div>
            </main>
        </div>
    );
}

export default Products;
