import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function UpdateGreaseProductForm({ productId, onUpdateProduct }) {
    const [productData, setProductData] = useState(null);
    const [loading, setLoading] = useState(true);  // To handle loading state
    const [error, setError] = useState(null);  // To handle errors

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/grease/${productId}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch product data');
                }
                const data = await response.json();
                setProductData(data);
                setLoading(false);
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        if (productId) {
            fetchProduct();
        }
    }, [productId]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setProductData({
            ...productData,
            [name]: type === 'checkbox' ? checked : value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`http://localhost:5000/api/grease/${productId}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(productData),
            });

            if (!response.ok) {
                const data = await response.json();
                throw new Error(data.error || 'Failed to update product');
            }

            const data = await response.json();
            alert(data.message || 'Product updated successfully!');
            onUpdateProduct();  // Refresh products
        } catch (error) {
            alert(error.message || 'An error occurred while updating the product');
        }
    };

    if (loading) return <p>Loading...</p>;  // Show loading message
    if (error) return <p style={{ color: 'red' }}>{error}</p>;  // Show error message
    if (!productData) return <p>No product data available</p>;

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>
                    Name:
                    <input
                        type="text"
                        name="name"
                        value={productData.name}
                        onChange={handleChange}
                        required
                    />
                </label>
            </div>

            <div>
                <label>
                    Category:
                    <input
                        type="text"
                        name="category"
                        value={productData.category}
                        onChange={handleChange}
                        required
                    />
                </label>
            </div>

            <div>
                <label>
                    Price:
                    <input
                        type="number"
                        name="price"
                        value={productData.price}
                        onChange={handleChange}
                        required
                    />
                </label>
            </div>

            <div>
                <label>
                    Stock:
                    <input
                        type="number"
                        name="stock"
                        value={productData.stock}
                        onChange={handleChange}
                        required
                    />
                </label>
            </div>

            <div>
                <label>
                    Image URL:
                    <input
                        type="text"
                        name="image"
                        value={productData.image}
                        onChange={handleChange}
                        required
                    />
                </label>
            </div>

            <div>
                <label>
                    Offer:
                    <input
                        type="checkbox"
                        name="offer"
                        checked={productData.offer}
                        onChange={handleChange}
                    />
                </label>
            </div>

            <button type="submit">Update Product</button>
        </form>
    );
}

export default UpdateGreaseProductForm;
