import React, { useState, useEffect } from "react";
import "./Products.css";

function Products() {
  const [category, setCategory] = useState("products"); // default = bearings
  const [products, setProducts] = useState([]);

  const fetchProducts = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/${category}`);
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error("Failed to fetch products:", error);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [category]);

  const getActiveClass = (cat) => (cat === category ? "active" : "");

  return (
    <div className="products-page">
      <h2>Our Products</h2>
      <p className="products-description">
        Browse our wide range of products across multiple categories.
      </p>

      <div className="category-buttons">
        <button
          onClick={() => setCategory("products")}
          className={`category-btn ${getActiveClass("products")}`}
        >
          Bearings
        </button>
        <button
          onClick={() => setCategory("grease")}
          className={`category-btn ${getActiveClass("grease")}`}
        >
          Grease
        </button>
        <button
          onClick={() => setCategory("blocks")}
          className={`category-btn ${getActiveClass("blocks")}`}
        >
          Blocks
        </button>
      </div>

      <div className="products-grid">
        {products.map((product, index) => (
          <div
            key={index}
            className={`product-card ${product.offer ? "offer" : ""}`}
          >
            <img
              src={product.image}
              alt={product.name}
              className="product-image"
            />
            <h3 className="product-title">{product.name}</h3>
            <p>Category: {product.category}</p>
            <p className="product-price">${product.price}</p>
            {product.originalPrice && (
              <p className="original-price">${product.originalPrice}</p>
            )}
            <button className="buy-now-button">Buy Now</button>
            {product.offer && <span className="offer-badge">Special Offer</span>}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Products;
