import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './AdminDashboard.css';
import AddGreaseProductForm from './AddGreaseProductForm';
import UpdateGreaseProductForm from './UpdateGreaseProductForm';
import DeleteGreaseProductForm from './DeleteGreaseProductForm'; // Import the delete form

function AdminDashboard() {
    const [enquiries, setEnquiries] = useState([]);
    const [greaseProducts, setGreaseProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [isAddingProduct, setIsAddingProduct] = useState(false);
    const [isDeletingProduct, setIsDeletingProduct] = useState(false);  // Track if delete form is shown
    const navigate = useNavigate();

    useEffect(() => {
        const fetchEnquiries = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/contact');
                const data = await response.json();
                if (response.ok) {
                    setEnquiries(data);
                    setLoading(false);
                } else {
                    throw new Error(data.message || 'Failed to fetch enquiries');
                }
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        const fetchGreaseProducts = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/grease');
                const data = await response.json();
                if (response.ok) {
                    setGreaseProducts(data);
                    setLoading(false);
                } else {
                    throw new Error(data.message || 'Failed to fetch grease products');
                }
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        fetchEnquiries();
        fetchGreaseProducts();
    }, []);

    const handleLogout = () => {
        localStorage.clear();
        sessionStorage.clear();
        navigate('/');
    };

    const handleDelete = (productId) => {
        setSelectedProduct(productId);
        setIsDeletingProduct(true);  // Show the delete form when delete button is clicked
    };

    const handleUpdateProduct = (productId) => {
        setSelectedProduct(productId);
    };

    const handleDeleteProduct = () => {
        setIsDeletingProduct(false);  // Close delete form after deletion
        fetchGreaseProducts(); // Refresh product list
    };

    const handleAddProduct = () => {
        setIsAddingProduct(true);
    };

    return (
        <div className="admin-container">
            <div className="dashboard-header">
                <h2>Customer Enquiries</h2>
                <button className="logout-button" onClick={handleLogout}>Logout</button>
            </div>

            {loading && <p>Loading...</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <div>
                <h3>Grease Products</h3>
                <button onClick={handleAddProduct}>Add New Grease Product</button>
                {isAddingProduct && <AddGreaseProductForm onAddProduct={() => setIsAddingProduct(false)} />}
                
                {/* Conditionally render the update form */}
                {selectedProduct && !isDeletingProduct && (
                    <UpdateGreaseProductForm productId={selectedProduct} onUpdateProduct={() => setSelectedProduct(null)} />
                )}

                {/* Conditionally render the delete form */}
                {isDeletingProduct && (
                    <DeleteGreaseProductForm productId={selectedProduct} onDeleteProduct={handleDeleteProduct} />
                )}

                {greaseProducts.length === 0 ? (
                    <p>No grease products found.</p>
                ) : (
                    <ul className="product-list">
                        {greaseProducts.map((product) => (
                            <li key={product._id} className="product-card">
                                <h4>{product.name}</h4>
                                <p>Price: ${product.price}</p>
                                <p>Stock: {product.stock}</p>
                                <p>Category: {product.category}</p>
                                <p>Offer: {product.offer ? 'Yes' : 'No'}</p>
                                <img src={product.image} alt={product.name} width="100" />
                                <button onClick={() => handleDelete(product._id)}>Delete</button>
                                <button onClick={() => handleUpdateProduct(product._id)}>Update</button>
                            </li>
                        ))}
                    </ul>
                )}
            </div>

            <div>
                <h3>Customer Enquiries</h3>
                {enquiries.length === 0 ? (
                    <p>No enquiries found.</p>
                ) : (
                    <ul className="enquiry-list">
                        {enquiries.map((enquiry, index) => (
                            <li key={index} className="enquiry-card">
                                <p><strong>Name:</strong> {enquiry.name}</p>
                                <p><strong>Email:</strong> {enquiry.email}</p>
                                <p><strong>Message:</strong> {enquiry.message}</p>
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
}

export default AdminDashboard;
