import React, { useEffect, useState } from 'react';
import './Admin.css'; // Optional: you can style this separately
import Navbar from './Navbar';

function Admin() {
    const [enquiries, setEnquiries] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchEnquiries = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/enquiries'); // ✅ Should match backend endpoint
                const data = await response.json();
                if (response.ok) {
                    setEnquiries(data);
                    setLoading(false);
                } else {
                    throw new Error(data.message || 'Failed to fetch enquiries');
                }
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        fetchEnquiries();
    }, []);

    return (
        <div>
            <Navbar />
            <div className="admin-container">
                <h2>Customer Enquiries</h2>
                {loading && <p>Loading enquiries...</p>}
                {error && <p style={{ color: 'red' }}>{error}</p>}
                {!loading && enquiries.length === 0 && <p>No enquiries found.</p>}
                <ul className="enquiry-list">
                    {enquiries.map((enquiry, index) => (
                        <li key={index} className="enquiry-card">
                            <p><strong>Name:</strong> {enquiry.name}</p>
                            <p><strong>Email:</strong> {enquiry.email}</p>
                            <p><strong>Message:</strong> {enquiry.message}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default Admin;
