import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Products.css';

function Products() {
    const [category, setCategory] = useState('bearings');
    const [items, setItems] = useState([]);
    const navigate = useNavigate();

    const handleLogout = () => {
        navigate('/signin');
    };

    useEffect(() => {
        const fetchData = async () => {
            let endpoint = '';
            if (category === 'bearings') endpoint = 'products';
            else if (category === 'grease') endpoint = 'grease';
            else if (category === 'blocks') endpoint = 'blocks';

            try {
                const res = await fetch(`http://localhost:5000/api/${endpoint}`);
                const data = await res.json();
                setItems(data);
            } catch (err) {
                console.error('Error fetching data:', err);
            }
        };

        fetchData();
    }, [category]);

    return (
        <div className="products-wrapper">
            {/* Navbar like Home.js */}
            <header className="navbar fixed-navbar">
                <div className="navbar-brand">Mehta Enterprise</div>
                <div className="navbar-menu">
                    <ul className="navbar-links">
                        <li><Link to="/home">Home</Link></li>
                        <li><Link to="/products">Products</Link></li>
                        <li><Link to="/contact">Contact Us</Link></li>
                    </ul>
                    <button className="logout-button" onClick={handleLogout}>Logout</button>
                </div>
            </header>

            <main className="products-page">
                <h2>Our Products</h2>
                <p className="products-description">Browse through our wide range of industrial bearings and products.</p>

                {/* Category Buttons */}
                <div className="category-buttons">
                    <button
                        className={`category-btn ${category === 'bearings' ? 'active' : ''}`}
                        onClick={() => setCategory('bearings')}
                    >
                        Bearings
                    </button>
                    <button
                        className={`category-btn ${category === 'grease' ? 'active' : ''}`}
                        onClick={() => setCategory('grease')}
                    >
                        Grease
                    </button>
                    <button
                        className={`category-btn ${category === 'blocks' ? 'active' : ''}`}
                        onClick={() => setCategory('blocks')}
                    >
                        Blocks
                    </button>
                </div>

                <div className="products-grid">
                    {items.map((item) => (
                        <div key={item._id} className={`product-card ${item.offer ? 'offer' : ''}`}>
                            <img src={item.image} alt={item.name} className="product-image" />
                            <h3>{item.name}</h3>
                            <p>Category: {item.category}</p>
                            <p>Price: ${item.price}</p>
                            {item.offer && <span className="offer-badge">Special Offer</span>}
                            <button className="buy-now-button">Buy Now</button>
                        </div>
                    ))}
                </div>
            </main>
        </div>
    );
}

export default Products;
