import React, { useState } from 'react';

function DeleteGreaseProductForm({ product, onClose, onSuccess }) {
    const [isDeleting, setIsDeleting] = useState(false);
    const [error, setError] = useState('');

    const handleDelete = async () => {
        if (!window.confirm(`Are you sure you want to delete "${product.name}"?`)) {
            return;
        }

        try {
            setIsDeleting(true);
            setError('');
            
            const response = await fetch(`http://localhost:5000/api/grease/${product._id}`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}` // Add if using auth
                },
            });

            // First check if response is JSON
            const contentType = response.headers.get('content-type');
            if (!contentType || !contentType.includes('application/json')) {
                const text = await response.text();
                throw new Error(text || 'Invalid response from server');
            }

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Failed to delete product');
            }

            onSuccess();
            alert(`"${product.name}" was deleted successfully!`);
        } catch (err) {
            setError(err.message || 'An error occurred while deleting the product');
            console.error('Delete error:', err);
        } finally {
            setIsDeleting(false);
        }
    };

    return (
        <div className="delete-form-container">
            <h3>Delete Product</h3>
            {error && <div className="error-message">{error}</div>}
            
            <div className="product-preview">
                <img src={product.image} alt={product.name} width="150" />
                <div className="product-details">
                    <p><strong>Name:</strong> {product.name}</p>
                    <p><strong>Category:</strong> {product.category}</p>
                    <p><strong>Price:</strong> ${product.price}</p>
                </div>
            </div>

            <div className="form-actions">
                <button 
                    onClick={handleDelete}
                    disabled={isDeleting}
                    className="delete-btn"
                >
                    {isDeleting ? 'Deleting...' : 'Confirm Delete'}
                </button>
                <button 
                    onClick={onClose}
                    className="cancel-btn"
                >
                    Cancel
                </button>
            </div>
        </div>
    );
}

export default DeleteGreaseProductForm;
