import React, { useEffect, useState } from 'react';
import './AdminDashboard.css';

function AdminDashboard() {
    const [enquiries, setEnquiries] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchEnquiries = async () => {
            try {
                // Fetching contact form submissions from the backend
                const response = await fetch('http://localhost:5000/api/contact'); 

                // Ensure the response is valid JSON
                const text = await response.text();
                const data = JSON.parse(text);

                if (response.ok) {
                    setEnquiries(data);  // Set the fetched data to state
                    setLoading(false);
                } else {
                    throw new Error(data.message || 'Failed to fetch enquiries');
                }
            } catch (err) {
                setError(err.message);
                setLoading(false);
            }
        };

        fetchEnquiries();
    }, []);  // Empty dependency array to run only once on component mount

    return (
        <div>
            <div className="admin-container">
                <h2>Customer Enquiries</h2>
                {loading && <p>Loading enquiries...</p>}
                {error && <p style={{ color: 'red' }}>{error}</p>}
                {!loading && enquiries.length === 0 && <p>No enquiries found.</p>}
                <ul className="enquiry-list">
                    {enquiries.map((enquiry, index) => (
                        <li key={index} className="enquiry-card">
                            <p><strong>Name:</strong> {enquiry.name}</p>
                            <p><strong>Email:</strong> {enquiry.email}</p>
                            <p><strong>Message:</strong> {enquiry.message}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default AdminDashboard;
